import React from 'react';
import { connect } from 'react-redux';
import { Redirect, Route } from 'react-router-dom';
import ApproveUser from '../../../Pages/ApproveUser/ApproveUser';

let PrivateRoute = (props) => {
  let { component: Component, ...rest } = props;
  let PrivateRouteComponent = <Component {...props} />;   //Welcome component as per 'App.js'
  let RedirectComponent = <Redirect to={{ pathname: props.redirectPath, state: { from: props.location }}} />;   //Back to Login as per 'App.js'

  //Conditional rendirect based on user role
  if(props.status === 'A' && props.userRoleId === 1){
    PrivateRouteComponent = <ApproveUser {...props} />
  }

  return (
    <Route render={() => (props.status === 'A') ? PrivateRouteComponent : RedirectComponent} {...rest} />
  );
}

let mapStateToProps = (state) => {
  return{
    status: (state.tempEdge.login !== "")? state.tempEdge.login.portalUserList[0].status: null,
    userRoleId: (state.tempEdge.login !== "")? state.tempEdge.login.portalUserList[0].userRoleId: null,
  };
}

export default connect(mapStateToProps)(PrivateRoute);
