import React from 'react';
import { withLocalize, Translate } from 'react-localize-redux';
import { connect } from 'react-redux';
import ActiveLanguageAddTranslation from '../ActiveLanguageAddTranslation/ActiveLanguageAddTranslation.js';

class NavPanelLeft extends React.Component{
  constructor(props){
    super(props);

    ActiveLanguageAddTranslation(this.props.activeLanguage, this.props.addTranslationForLanguage);
  }

  componentDidUpdate(prevProps, prevState){
    const hasActiveLanguageChanged = prevProps.activeLanguage !== this.props.activeLanguage;

    if(hasActiveLanguageChanged) {
      // this.props.push(`/register/${this.props.activeLanguage.code}`);
      ActiveLanguageAddTranslation(this.props.activeLanguage, this.props.addTranslationForLanguage);
    }
  }

  toggleNav = () => {
    this.props.togglePanelNav();
  }

  render(){
    let panelClass = (this.props.show)? "panel-nav-left show": "panel-nav-left ";

    return(
      <React.Fragment>
        <nav className={panelClass}>
          <button onClick={this.toggleNav} className="pull-right">Open</button>
          <ul>
            <li><a href="/">Products</a></li>
            <li><a href="/">Users</a></li>
          </ul>
        </nav>
      </React.Fragment>
    );
  }
}

export default withLocalize(connect(null)(NavPanelLeft));
